import dayjs from "dayjs";
export var thumbnail = function thumbnail(images) {
  return images[0].replace(/\/original\//, "/thumb/");
};
export var decodeHTML = function decodeHTML(item) {
  var content = item.html.replace(/(<([^>]+)>)/gi, "");

  if (item.image && item.image.length > 0) {
    return content.substring(0, 150);
  } else {
    return content.substring(0, 500);
  }
};
export var decodeHTMLForHeader = function decodeHTMLForHeader(html) {
  var content = html.replace(/(<([^>]+)>)/gi, "");
  return content.substring(0, 150);
};
export var dateDisplay = function dateDisplay(date) {
  var nowDate = new Date();
  var compareDate = new Date(date);
  var dayGap = nowDate.getDate() - compareDate.getDate();
  return dayGap > 7 ? dayjs(date).format("YYYY년 MM월 DD일 hh시 mm분 ss초") : dayjs(date).fromNow();
};
