                <LoginInput name={"name"} placeholder={"이름"} onChange={handleRegisterInput} />
              </LoginInputWrap>
              <LoginInputWrap>
                <LoginInput name={"password"} type={"password"} placeholder={"비밀번호"} onChange={handleRegisterInput} />
              </LoginInputWrap>
