            </LoginModalBody>
            <LoginModalFooter>
              <Register onClick={changeToLoginModal}>로그인</Register>
            </LoginModalFooter>
          </LoginModal>
        </LoginModalContainer>
      )}
    </HeaderContainer>
  );
};

